const salesData = $('Voice_TimeStamp').item.json.message.content;

// Required fields to check
const requiredFields = [
    "Retailer Name",
    "Retailer Contact Person", 
    "Retailer Feedback", 
    "Competitor Feedback"
];

// Find missing fields
const missingFields = [];
requiredFields.forEach(field => {
    const value = salesData[field];
    if (!value || value.toString().trim() === "" || value === "N/A" || value === "Not Provided") {
        missingFields.push(field);
    }
});

// Create request message
let requestMessage;
if (missingFields.length === 0) {
    requestMessage = "All fields are complete!";
} else if (missingFields.length === 1) {
    requestMessage = `Could you please provide the ${missingFields[0]}?`;
} else {
    const allButLast = missingFields.slice(0, -1).join(", ");
    const lastField = missingFields[missingFields.length - 1];
    requestMessage = `Could you please provide the ${allButLast} and ${lastField}?`;
}

// Create unique key
const dateTime = salesData["Date&TimeStamp"] ;
// const retailer = salesData["Retailer Name"] || "unknown"; 
const uniqueKey = DateTime ; 
  //`${dateTime}_${retailer}`;

// Return the result
return {
    ...salesData,
    missing_fields: missingFields,
    request: requestMessage,
    has_missing: missingFields.length > 0,
    unique_key: uniqueKey
};

